// App.js
import {React,useEffect,useState,useNavigate} from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { Signup } from './component/Signup';
import { Login } from './component/Login';
import { NavbarComp } from './components/NavbarComp';
import { API } from './components/API';
import { Myfortfolio } from './components/Myfortfolio';
import { MutualFunds } from './components/MutualFunds';

function App() {
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    // Check if the user is authenticated from local storage
    const token = localStorage.getItem('Token:');
    if (token) {
      setAuthenticated(true);
    }
  }, []);

  const navigate = useNavigate();

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/home" element={authenticated ? <NavbarComp /> : <Navigate to="/" replace />}/>
        <Route path="/api" element={<API />} />
        <Route path="/my-portfolio" element={<Myfortfolio />} />
        <Route path="/mutual-funds" element={<MutualFunds />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
