import {React, useEffect, useState} from 'react';
import { Container } from 'react-bootstrap';
import { Navbar, Nav, Button} from "react-bootstrap";
import { Link } from "react-router-dom";
import { isAuthenticated, handleLogout } from "../component/Login";
import { useNavigate } from "react-router-dom";
import axios from 'axios'; // Import axios for making HTTP requests

export const Myfortfolio = () => {
  const [navbar, setNavbar] = useState(false);
  const [myfortfolioData, setmyfortfolioData] = useState(null); // State to store profile data
  const navigate = useNavigate();

  const changeBackground = () => {
    if (window.scrollY >= 80) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };

  window.addEventListener("scroll", changeBackground);

  useEffect(() => {
    if (!localStorage.getItem("Jsonwebtoken")) {
      navigate("/");
    }
  }, [navigate]);

  useEffect(() => {
    // Function to fetch profile data
    const fetchmyfortfolioData = async () => {
      try {
        const response = await axios.get('/auth/myfortfolio', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("Jsonwebtoken")}` // Attach JWT token to request headers
          }
        });
        setmyfortfolioData(response.data); // Set profile data in state
      } catch (error) {
        console.error('Error fetching profile data:', error);
      }
    };

    // Check if user is authenticated and fetch profile data
    if (isAuthenticated()) {
      fetchmyfortfolioData();
    }
  }, []); // Empty dependency array ensures this effect runs only once on component mount

  return (
    <>
      <div>
        <Navbar className={navbar ? "navbar active" : "navbar"} sticky="top">
          <Navbar.Brand>
            <img src="images/logo.png" alt="Logo" height="45px"/>
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Nav className="me-auto">
            <Link className="MFSubhead mx-3" to="/mutual-funds">
              Mutual Fund
            </Link>
            <Link className="MFSubhead mx-3" to="/my-portfolio">
              My Portfolio
            </Link>
            <Link className="MFSubhead mx-3" to="/api">
              Api
            </Link>
          </Nav>
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              {isAuthenticated() && (
                <Button variant="outline-light" onClick={() => handleLogout(navigate)}>
                  Logout
                </Button>
              )}
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </div>
      <Container fluid>
        <h1>Myfortfolio</h1>
        {/* Render profile data */}
        {myfortfolioData && (
          <div>
            { <h1>meassage: {myfortfolioData.data}</h1>}
            {/* Example: <p>Name: {profileData.name}</p> */}
          </div>
        )}
      </Container>
    </>
  );
};
