import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import "./App.css";
import { Signup } from "./component/Signup";
import { Login } from "./component/Login";
import { NavbarComp } from "./components/NavbarComp";
import { API } from "./components/API";
import { Myfortfolio } from "./components/Myfortfolio";
import { MutualFunds } from "./components/MutualFunds";

// Check if the user is authenticated
const isAuthenticated = () => {
  return localStorage.getItem("Jsonwebtoken") !== null;
};

// Protected Route component
const ProtectedRoute = ({ element, ...rest }) => {
  return isAuthenticated() ? (
    element
  ) : (
    <Navigate to="/" replace />
  );
};

function App() {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route
          path="/home"
          element={<ProtectedRoute element={<NavbarComp />} />}
        />
        <Route
          path="/api"
          element={<ProtectedRoute element={<API />} />}
        />
        <Route
          path="/my-portfolio"
          element={<ProtectedRoute element={<Myfortfolio />} />}
        />
        <Route
          path="/mutual-funds"
          element={<ProtectedRoute element={<MutualFunds />} />}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
