import React, { useState, useEffect } from "react";
import { Container } from "react-bootstrap";
import "./NavbarComp.css";
import { Navbar, Nav } from "react-bootstrap";
import { Link } from "react-router-dom";

export const MutualFunds = () => {
  const [navbar, setNavbar] = useState(false);

  const changeBackground = () => {
    if (window.scrollY >= 80) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };

  window.addEventListener("scroll", changeBackground);

  const [count, setCount] = useState(0);
  const [text, setText] = useState(null);

  useEffect(() => {
    // alert("this is alert for useefect")
    console.log("The Count is:", count);
  }, [count]);

  const upperCase = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const lowerCase = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
  const clearText = () => {
    let Ctext = "";
    setText(Ctext);
  };

  const onChangeHandler = (event) => {
    setText(event.target.value);
  };

  return (
    <>
      <div>
        <Navbar className={navbar ? "navbar active" : "navbar"} sticky="top">
          <Navbar.Brand>
            <img src="images/logo.png" alt="Logo" height="45px" />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />

          <Nav className="me-auto">
            <Link className="MFSubhead mx-3" to="/mutual-funds">
              Mutual Fund
            </Link>
            <Link className="MFSubhead mx-3" to="/my-portfolio">
              My Portfolio
            </Link>
            <Link className="MFSubhead mx-3" to="/api">
              Api
            </Link>
          </Nav>
        </Navbar>
      </div>
      <Container>
        <h1>This is Mutual fund page</h1>
        <div className="container">
          <p>Count : {count}</p>
          <button onClick={() => setCount(count + 1)}>Click Me</button>

          <form>
            <label htmlFor="input" className="form-label my-3">
              Enter Text
            </label>
            <input
              type="input"
              value={text}
              onChange={onChangeHandler}
              className="form-control"
            />
            <button
              type="button"
              className="button btn-bg-primary my-3 mx-3"
              onClick={upperCase}
            >
              Convert UpperCase
            </button>
            <button
              type="button"
              className="button btn-bg-primary my-3 mx-3"
              onClick={lowerCase}
            >
              Convert LowerCase
            </button>
            <button
              type="button"
              className="button btn-bg-primary my-3 "
              onClick={clearText}
            >
              Clear Text
            </button>
            <h2>Preview</h2>
            <p>{text}</p>
          </form>
        </div>
      </Container>
    </>
  );
};
