import React, { useState } from "react";
import "./NavbarComp.css";
import { Navbar, Nav, Container } from "react-bootstrap";
import { BrowserRouter, Routes, Link, Route } from "react-router-dom"; // Import BrowserRouter and Routes


function NavbarComp() {
  const [navbar, setNavbar] = useState(false);

  const changeBackground = () => {
    if (window.scrollY >= 80) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };

  window.addEventListener("scroll", changeBackground);

  return (
    <BrowserRouter> {/* Wrap your Routes with BrowserRouter */}
      <div>
        <Navbar className={navbar ? "navbar active" : "navbar"} sticky="top">
          <Navbar.Brand as={Link} to="/">
            {/* Add a link to the home page */}
            <img src="images/logo.png" alt="Logo" height="45px" />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Container>
              <Nav className="me-auto">
                <Nav.Link className="MFHead" as={Link} to="/mutual-funds">
                  Mutual Fund
                </Nav.Link>
                <Nav.Link className="MFSubhead" as={Link} to="/cookis-sesions">
                  Cookis Sessions
                </Nav.Link>
                <Nav.Link className="MFSubhead" as={Link} to="/my-portfolio">
                  My Portfolio
                </Nav.Link>
                <Nav.Link className="MFSubhead" as={Link} to="/blog">
                  Blog
                </Nav.Link>
                <Nav.Link className="MFSubhead" as={Link} to="/api">
                  Api
                </Nav.Link>
              </Nav>
            </Container>
          </Navbar.Collapse>
        </Navbar>
        <div>
          <Routes>
            <Route path="/mutual-funds" element={<MutualFunds/>} />
            <Route path="/cookis-sesions" element={<CookisSesions/>} />
            <Route path="/my-portfolio" element={<Myfortfolio />} />
            <Route path="/blog" element={<Blog />} />
            <Route path="/api" element={<API />} />
          </Routes>
        </div>
      </div>
    </BrowserRouter>
  );
}

export default NavbarComp;
