import React, { useEffect, useState } from 'react';
import { Container } from 'react-bootstrap';
// import { Navbar, Nav, Button } from 'react-bootstrap';
// import { Link } from 'react-router-dom';
import { isAuthenticated } from '../component/Login';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export const Myfortfolio = () => {
  // const [navbar, setNavbar] = useState(false);
  const [profileData, setProfileData] = useState(null);
  const navigate = useNavigate();

  // const changeBackground = () => {
  //   if (window.scrollY >= 80) {
  //     setNavbar(true);
  //   } else {
  //     setNavbar(false);
  //   }
  // };

  // window.addEventListener('scroll', changeBackground);

  useEffect(() => {
    if (!localStorage.getItem('Jsonwebtoken')) {
      navigate('/');
    }
  }, [navigate]);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/auth/myfortfolio', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('Jsonwebtoken')}`,
          },
        });
        setProfileData(response.data);
        // console.log(response.data)
      } catch (error) {
        console.error('Error fetching profile data:', error);
      }
    };
    

    if (isAuthenticated()) {
      fetchProfileData();
    }
  }, []);

  return (
    <>
      {/* <div> */}
        {/* <Navbar className={navbar ? 'navbar active' : 'navbar'} sticky="top">
          <Navbar.Brand>
            <img src="images/logo.png" alt="Logo" height="45px" />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Nav className="me-auto">
            <Link className="MFSubhead mx-3" to="/mutual-funds">
              Mutual Fund
            </Link>
            <Link className="MFSubhead mx-3" to="/my-portfolio">
              My Portfolio
            </Link>
            <Link className="MFSubhead mx-3" to="/api">
              Api
            </Link>
          </Nav>
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              {isAuthenticated() && (
                <Button variant="outline-light" onClick={() => handleLogout(navigate)}>
                  Logout
                </Button>
              )}
            </Nav>
          </Navbar.Collapse>
        </Navbar> */}
      {/* </div> */}
      <Container fluid>
        {/* Render profile data */}
        {profileData && (
          <div>
            <h4>Message: {profileData}</h4>
          </div>
        )}
      </Container>
    </>
  );
};
