import React, { useEffect, useState } from 'react';
import { Container, Row } from 'react-bootstrap';
import { Navbar, Nav, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { isAuthenticated, handleLogout } from '../component/Login';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export const Myfortfolio = () => {
  const [navbar, setNavbar] = useState(false);
  const [profileData, setProfileData] = useState(null);
  const navigate = useNavigate();

  const changeBackground = () => {
    if (window.scrollY >= 80) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };

  window.addEventListener('scroll', changeBackground);

  useEffect(() => {
    if (!localStorage.getItem('Jsonwebtoken')) {
      navigate('/');
    }
  }, [navigate]);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/auth/myfortfolio', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('Jsonwebtoken')}`,
          },
        });
        // console.log(response)
        setProfileData(response.data);
        // console.log(response.data)
      } catch (error) {
        console.error('Error fetching profile data:', error);
        // Check if the error is due to unauthorized access (token tampering)
        if (error.response && error.response.status === 401) {
          localStorage.removeItem('Jsonwebtoken');
          // Redirect the user to the login page
          navigate('/');
        }
      }
    };
  
    if (isAuthenticated()) {
      fetchProfileData();
    }
  }, [navigate]);
  

  return (
    <>
      {/* <div>
        <Navbar className={navbar ? 'navbar active' : 'navbar'} sticky="top
        ">
          <Navbar.Brand>
            <img src="images/logo.png" alt="Logo" height="45px" />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Nav className="me-auto">
            <Link className="MFSubhead mx-3" to="/mutual-funds">
              Mutual Fund
            </Link>
            <Link className="MFSubhead mx-3" to="/my-portfolio">
              My Portfolio
            </Link>
            <Link className="MFSubhead mx-3" to="/api">
              Api
            </Link>
            <Link className="MFSubhead mx-3" to="/blog">
            Blog
          </Link>
          </Nav>
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              {isAuthenticated() && (
                <Button variant="outline-light" onClick={() => handleLogout(navigate)}>
                  Logout
                </Button>
              )}
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </div> */}
      <Container fluid>
        <Row>
        {/* Render profile data */}
        {profileData && (
          <div className='bg-dark text-white'>
            <h1> {profileData.message}</h1>
          </div>
         
        )}
         </Row>
         <div className='container'>
          <h2 className='pt-4'>My Portfolio</h2>
          <h3>Welcome to my portfolio!</h3>
          <img src='images\myfortfolio.jpg' alt='img'/>
        <p >This is an example of a portfolio. Portfolios use evidence of your education, works samples and skills to tell a carefully crafted story to the world about who you are and what you can do. Portfolios contain an organized collection of artifacts to tell that story. Portfolios can include text, photos, audio, and video...be creative and tell your story of your accomplishments!</p>
        </div>
      </Container>
    </>
  );
};
