import React, { useState } from "react";
import { BrowserRouter, Routes, Route, useNavigate } from "react-router-dom";
import "./App.css";
import { Signup } from "./component/Signup";
import { Login } from "./component/Login";
import { NavbarComp } from "./components/NavbarComp";
import { API } from "./components/API";
import { Myfortfolio } from "./components/Myfortfolio";
import { Blog } from "./components/Blog";
import { MutualFunds } from "./components/MutualFunds";
import { Home } from "./components/Home";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  // Function to handle login
  const handleLogin = () => {
    // Perform login logic here
    // For example, set isLoggedIn to true after successful login
    setIsLoggedIn(true);

    // Redirect to the desired page after successful login
    navigate("/home");
  };

  // Function to handle logout
  const handleLogout = () => {
    // Perform logout logic here
    // For example, set isLoggedIn to false after logout
    setIsLoggedIn(false);
  };

  return (
    <BrowserRouter>
      <Routes>
        {/* Route for the login page */}
        <Route
          path="/"
          element={isLoggedIn ? <Home /> : <Login onLogin={handleLogin} />}
        />
        <Route path="/signup" element={<Signup />} />
        {/* Protected routes */}
        {isLoggedIn && (
          <>
            <Route path="/home" element={<NavbarComp />} />
            <Route path="/api" element={<API />} />
            <Route path="/my-portfolio" element={<Myfortfolio />} />
            <Route path="/blog" element={<Blog />} />
            <Route path="/mutual-funds" element={<MutualFunds />} />
          </>
        )}
        {/* Add a default route for the home page */}
        {!isLoggedIn && <Route path="/*" element={<Login onLogin={handleLogin} />} />}
      </Routes>
    </BrowserRouter>
  );
}

export default App;
