import React, { useState , useEffect } from 'react'
import { Container } from 'react-bootstrap';

export const MutualFunds = () => {
  const [count , setCount]=useState(0);
  const [text, setText] = useState(null)

  useEffect(() => {
  // alert("this is alert for useefect")
   console.log('The Count is:',count)
  }, [count])
  

  const upperCase = ()=>{
    let newText = text.toUpperCase();
    setText(newText)
  }
  const lowerCase = ()=>{
    let newText = text.toLowerCase();
    setText(newText)
  }
  const clearText =()=>{
    let Ctext ="";
    setText(Ctext)
  }

  const onChangeHandler = (event) =>{
    setText(event.target.value);
  }
  
  return (
    <>
   
    <Container>
    <h1>This is Mutual fund page</h1>
    <div className="container">
    <p>Count : {count}</p>
    <button onClick={()=>setCount(count +1)}>Click Me</button>

    <form>
    <label htmlFor="input" className="form-label my-3">Enter Text</label>
    <input type="input" value={text} onChange={onChangeHandler} className="form-control"/> 
    <button type="button" className="button btn-bg-primary my-3 mx-3" onClick={upperCase}>Convert UpperCase</button>
    <button type="button" className="button btn-bg-primary my-3 mx-3" onClick={lowerCase}>Convert LowerCase</button>
    <button type="button" className="button btn-bg-primary my-3 " onClick={clearText}>Clear Text</button>
    <h2>Preview</h2>
    <p>{text}</p>
    </form>
    </div>
    </Container>
    </>
  )
}
