import React, { useState,useEffect } from "react";
import { Button, Container } from "react-bootstrap";
import "./NavbarComp.css";
import { Navbar, Nav } from "react-bootstrap";
import { Link } from "react-router-dom";
import { isAuthenticated, handleLogout } from "../component/Login"; // Import isAuthenticated and handleLogout functions
import { useNavigate } from "react-router-dom";
import axios from 'axios';

export const API = () => {

  const [navbar, setNavbar] = useState(false);
  const [apidata,setApidata] = useState(null);

  const changeBackground = () => {
    if (window.scrollY >= 80) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };

  window.addEventListener("scroll", changeBackground);

  const [api, setApi] = useState([]);

  const fetchData = () => {
    fetch("https://api.restful-api.dev/objects")
      .then((res) => res.json())
      .then((data) => {
        setApi(data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  };
  const navigate = useNavigate();

  useEffect(() => {
    if (!localStorage.getItem("Jsonwebtoken")) {
      navigate("/");
    }
  }, [navigate]);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/auth/myfortfolio', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('Jsonwebtoken')}`,
          },
        });
        // console.log(response)
        setApidata(response.data);
        // console.log(response.data)
      } catch (error) {
        console.error('Error fetching profile data:', error);
        // Check if the error is due to unauthorized access (token tampering)
        if (error.response && error.response.status === 401) {
          localStorage.removeItem('Jsonwebtoken');
          // Redirect the user to the login page
          navigate('/');
        }
      }
    };
  
    if (isAuthenticated()) {
      fetchProfileData();
    }
  });

  return (
    <>
    <div>
        <Navbar className={navbar ? "navbar active" : "navbar"} sticky="top">
          <Navbar.Brand >
           <img src="images/logo.png" alt="Logo" height="45px"/>
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />

              <Nav className="me-auto">
                <Link className="MFSubhead mx-3" to="/mutual-funds">
                  Mutual Fund
                </Link>
                <Link className="MFSubhead mx-3" to="/my-portfolio">
                  My Portfolio
                </Link>
                <Link className="MFSubhead mx-3" to="/api">
                  Api
                </Link>
                <Link className="MFSubhead mx-3" to="/blog">
            Blog
          </Link>
                <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">{/* Your navigation links */}</Nav>
            {isAuthenticated() && (
              <Button
                variant="outline-light"
                onClick={() => handleLogout(navigate)}
              >
                Logout
              </Button>
            )}
          </Navbar.Collapse>
              </Nav>
            
        </Navbar>
        </div>{ apidata &&
        <h2>{apidata.data}</h2>}
      <Button variant="info" onClick={fetchData} className="my-5 mx-5">
        Fetch Data
      </Button>
      {/* <Button variant="info" onClick={postData} className="my-5 mx-5">
        Post Data
      </Button> */}
      <Container
        style={{
          background: "#d1ecf1",
          padding: "10px",
          borderRadius: "5px",
          marginTop: "10px",
          width: "900px",
        }}
      >
        <pre style={{ whiteSpace: "pre-wrap", overflowX: "auto" }}>
          {JSON.stringify(api, null, 2)}
        </pre>
      </Container>
    </>
  );
};
